```typescript
import { julianToGregorian, gregorianToJulian } from "calendar-convert";

export function normalizeDate(dateStr: string, fromCalendar: "julian" | "gregorian" = "julian"): string {
  const year = parseInt(dateStr.split("-")[0]);
  // –î–æ 1582 –≥–æ–¥–∞ –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é —é–ª–∏–∞–Ω—Å–∫–∏–π –∫–∞–ª–µ–Ω–¥–∞—Ä—å
  if (year < 1582 && fromCalendar === "gregorian") {
    return julianToGregorian(dateStr);
  }
  return fromCalendar === "julian" ? julianToGregorian(dateStr) : dateStr;
}

// –ü—Ä–∏–º–µ—Ä –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è
console.log(normalizeDate("1901-01-20", "julian")); // "1901-02-02"
```

