```typescript
// pages/api/gigachat-recommendations.ts
import { NextApiRequest, NextApiResponse } from 'next';
import axios from 'axios';

const GIGACHAT_API_URL = 'https://gigachat.devices.sberbank.ru/api/v1/chat/completions';

export default async function handler(
  req: NextApiRequest,
  res: NextApiResponse
) {
  if (req.headers.authorization !== `Bearer ${process.env.ADMIN_SECRET}`) {
    return res.status(401).json({ error: 'Unauthorized' });
  }

  try {
    const accessToken = await getGigaChatToken();

    const response = await axios.post(
      GIGACHAT_API_URL,
      {
        model: 'GigaChat',
        messages: [
          {
            role: 'system',
            content: '–¢—ã ‚Äî —ç–∫—Å–ø–µ—Ä—Ç –ø–æ –∏—Å—Ç–æ—Ä–∏–∏ –º—É–∑—ã–∫–∏. –ê–Ω–∞–ª–∏–∑–∏—Ä—É–π –¥–∞–Ω–Ω—ã–µ –∏ –ø—Ä–µ–¥–ª–∞–≥–∞–π —É–ª—É—á—à–µ–Ω–∏—è –≤ JSON —Ñ–æ—Ä–º–∞—Ç–µ.'
          },
          {
            role: 'user',
            content: `–ü—Ä–æ–∞–Ω–∞–ª–∏–∑–∏—Ä—É–π —ç—Ç–∏ –¥–∞–Ω–Ω—ã–µ: ${JSON.stringify(req.body.data)}`
          }
        ],
        temperature: 0.3,
        max_tokens: 2000,
        response_format: { type: 'json_object' }
      },
      {
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${accessToken}`
        }
      }
    );

    res.status(200).json(JSON.parse(response.data.choices[0].message.content));

  } catch (error) {
    console.error('GigaChat Error:', error);
    res.status(500).json({ error: '–û—à–∏–±–∫–∞ GigaChat –∞–Ω–∞–ª–∏–∑–∞' });
  }
}

async function getGigaChatToken() {
  const response = await axios.post(
    'https://ngw.devices.sberbank.ru:9443/api/v2/oauth',
    new URLSearchParams({
      scope: 'GIGACHAT_API_CORP'
    }),
    {
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded',
        'RqUID': crypto.randomUUID(),
        'Authorization': `Basic ${Buffer.from(
          `${process.env.GIGACHAT_CLIENT_ID}:${process.env.GIGACHAT_CLIENT_SECRET}`
        ).toString('base64')}`
      },
      httpsAgent: new https.Agent({ rejectUnauthorized: false })
    }
  );
  return response.data.access_token;
}
```

