import { pipeline } from '@huggingface/transformers';
import { QuestionAnsweringModel } from '@deepen-ai/deeppavlov';

// –ó–∞–≥—Ä—É–∂–∞–µ–º –º–æ–¥–µ–ª—å –æ–¥–∏–Ω —Ä–∞–∑ –ø—Ä–∏ —Å—Ç–∞—Ä—Ç–µ
let qaModel: any = null;

export async function initLocalAI() {
  if (!qaModel) {
    qaModel = await pipeline(
      'question-answering',
