export async function analyzeDataLocally( any) {
  await initLocalAI();

  // –ü—Ä–∏–º–µ—Ä –∞–Ω–∞–ª–∏–∑–∞: –ø–æ–∏—Å–∫ –ø—Ä–æ–ø—É—â–µ–Ω–Ω—ã—Ö –¥–∞—Ç —Å–º–µ—Ä—Ç–∏
  const peopleWithoutDeath = data.people.filter((p: any) => !p.death);

  // –ê–Ω–∞–ª–∏–∑ —á–µ—Ä–µ–∑ QA –º–æ–¥–µ–ª—å
  const questions = [
    "–ö–∞–∫–∏–µ —Å–∫—Ä–∏–ø–∞—á–∏ –∏–∑ —Å–ø–∏—Å–∫–∞ –Ω–µ –∏–º–µ—é—Ç –¥–∞—Ç—ã —Å–º–µ—Ä—Ç–∏?",
