    "–ö–∞–∫–∏–µ —É—á–∏—Ç–µ–ª—è –Ω–µ —Å–≤—è–∑–∞–Ω—ã —Å–æ —Å–≤–æ–∏–º–∏ —É—á–µ–Ω–∏–∫–∞–º–∏?"
  ];

  const results = [];
  for (const question of questions) {
    try {
      const answer = await qaModel({
        question,
        context: JSON.stringify(data.people.slice(0, 10))
      });
      results.push({ question, answer });
    } catch (error) {
      console.error('–û—à–∏–±–∫–∞ –∞–Ω–∞–ª–∏–∑–∞:', error);
