```typescript
import { NextApiRequest, NextApiResponse } from 'next';
import jwt from 'jsonwebtoken';
import { YandexGPT } from '@yandex-cloud/nodejs-sdk';

// –í–∞–ª–∏–¥–∞—Ü–∏—è —Ç–æ–∫–µ–Ω–∞
function validateToken(token: string | undefined) {
  if (!token) return null;

  try {
    const decoded = jwt.verify(token.split(' ')[1], process.env.JWT_SECRET!);
    return decoded as { githubId: string };
  } catch (error) {
    return null;
  }
}

export default async function handler(
  req: NextApiRequest,
  res: NextApiResponse
) {
  // 1. –ü—Ä–æ–≤–µ—Ä–∫–∞ –º–µ—Ç–æ–¥–∞
  if (req.method !== 'POST') {
    return res.status(405).json({ error: 'Method not allowed' });
  }

  // 2. –í–∞–ª–∏–¥–∞—Ü–∏—è —Ç–æ–∫–µ–Ω–∞
  const authHeader = req.headers.authorization;
  const user = validateToken(authHeader);

  if (!user || !process.env.ADMIN_GITHUB_IDS?.includes(user.githubId)) {
    return res.status(401).json({ error: 'Unauthorized' });
  }

  // 3. –í–∞–ª–∏–¥–∞—Ü–∏—è —Ä–∞–∑–º–µ—Ä–∞ –∑–∞–ø—Ä–æ—Å–∞ (–∑–∞—â–∏—Ç–∞ –æ—Ç DDoS)
  const bodySize = JSON.stringify(req.body).length;
  if (bodySize > 100 * 1024) { // 100KB
    return res.status(413).json({ error: 'Request too large' });
  }

  // 4. Rate limiting (10 –∑–∞–ø—Ä–æ—Å–æ–≤ –≤ –º–∏–Ω—É—Ç—É)
  const rateLimitKey = `ratelimit:${user.githubId}:${Math.floor(Date.now() / 60000)}`;
  const currentRequests = (await getFromCache(rateLimitKey)) || 0;

  if (currentRequests >= 10) {
    return res.status(429).json({ 
      error: 'Too many requests', 
      retry_after: 60 - (Date.now() % 60000) / 1000 
    });
  }

  await setToCache(rateLimitKey, currentRequests + 1, 60); // 60 —Å–µ–∫—É–Ω–¥

  try {
    // 5. –ë–µ–∑–æ–ø–∞—Å–Ω–æ–µ –ø–æ–ª—É—á–µ–Ω–∏–µ –¥–∞–Ω–Ω—ã—Ö (—Ç–æ–ª—å–∫–æ –Ω—É–∂–Ω—ã–µ –ø–æ–ª—è)
    const { data } = req.body;
    const relevantData = {
      people_count: data.people?.length || 0,
      events_count: data.events?.length || 0,
      last_update: new Date().toISOString()
    };

    // 6. –í—ã–∑–æ–≤ Yandex GPT (—Ç–æ–∫–µ–Ω —Ö—Ä–∞–Ω–∏—Ç—Å—è –≤ Vercel Secrets)
    const sdk = new YandexGPT({ iamToken: process.env.YANDEX_GPT_IAM_TOKEN! });
    const response = await sdk.sendText({
      folderId: process.env.YANDEX_GPT_FOLDER_ID!,
      messages: [{
        role: 'user',
        text: `–ü—Ä–æ–∞–Ω–∞–ª–∏–∑–∏—Ä—É–π —ç—Ç–∏ –¥–∞–Ω–Ω—ã–µ: ${JSON.stringify(relevantData)}`
      }],
      temperature: 0.3,
      maxTokens: 1000,
    });

    // 7. –í–∞–ª–∏–¥–∞—Ü–∏—è –æ—Ç–≤–µ—Ç–∞
    const aiResponse = JSON.parse(response.text);
    if (!validateAIResponse(aiResponse)) {
      throw new Error('Invalid AI response format');
    }

    // 8. –õ–æ–≥–∏—Ä–æ–≤–∞–Ω–∏–µ –¥–ª—è –∞—É–¥–∏—Ç–∞ (–±–µ–∑ –ø–µ—Ä—Å–æ–Ω–∞–ª—å–Ω—ã—Ö –¥–∞–Ω–Ω—ã—Ö)
    await logAIRequest(user.githubId, relevantData, aiResponse);

    res.status(200).json(aiResponse);

  } catch (error) {
    console.error('AI API Error:', error);

    // 9. –ë–µ–∑–æ–ø–∞—Å–Ω—ã–π –æ—Ç–≤–µ—Ç –æ–± –æ—à–∏–±–∫–µ
    res.status(500).json({ 
      error: 'AI analysis failed', 
      timestamp: new Date().toISOString()
    });
  }
}

// –í—Å–ø–æ–º–æ–≥–∞—Ç–µ–ª—å–Ω—ã–µ —Ñ—É–Ω–∫—Ü–∏–∏
async function getFromCache(key: string): Promise<number | null> {
  // –ò—Å–ø–æ–ª—å–∑—É–µ–º Vercel KV (–±–µ—Å–ø–ª–∞—Ç–Ω–æ –¥–æ 3k –æ–ø–µ—Ä–∞—Ü–∏–π/–¥–µ–Ω—å)
  if (process.env.VERCEL_KV_REST_API_URL) {
    const response = await fetch(`${process.env.VERCEL_KV_REST_API_URL}/get/${key}`, {
      headers: { Authorization: `Bearer ${process.env.VERCEL_KV_REST_API_TOKEN}` }
    });
    const data = await response.json();
    return data.value ? parseInt(data.value) : null;
  }
  return null;
}

function validateAIResponse(response: any): boolean {
  // –ü—Ä–æ–≤–µ—Ä—è–µ–º –æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã–µ –ø–æ–ª—è
  return response && 
         typeof response === 'object' &&
         Array.isArray(response.errors) &&
         Array.isArray(response.new_events);
}
