import { NextApiRequest, NextApiResponse } from 'next';
import jwt from 'jsonwebtoken';

export function withAdminProtection(handler: (req: NextApiRequest, res: NextApiResponse, user: any) => Promise<void>) {
  return async (req: NextApiRequest, res: NextApiResponse) => {
    try {
      // –í–∞–ª–∏–¥–∞—Ü–∏—è —Ç–æ–∫–µ–Ω–∞
      const authHeader = req.headers.authorization;
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return res.status(401).json({ error: 'Missing authorization header' });
      }

      const token = authHeader.split(' ')[1];
      const decoded = jwt.verify(token, process.env.JWT_SECRET!);

      // –ü—Ä–æ–≤–µ—Ä–∫–∞ –ø—Ä–∞–≤ –∞–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä–∞
      const user = decoded as { githubId: string };
      if (!process.env.ADMIN_GITHUB_IDS?.includes(user.githubId)) {
        return res.status(403).json({ error: 'Forbidden' });
      }

      // –í—ã–∑–æ–≤ –∑–∞—â–∏—â–µ–Ω–Ω–æ–≥–æ –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–∞
      await handler(req, res, user);

    } catch (error) {
      console.error('API Protection Error:', error);
      if (error instanceof jwt.TokenExpiredError) {
        return res.status(401).json({ error: 'Token expired' });
      }
      if (error instanceof jwt.JsonWebTokenError) {
        return res.status(401).json({ error: 'Invalid token' });
      }
      res.status(500).json({ error: 'Internal server error' });
    }
  };
}

