```typescript
import crypto from 'crypto';

// –ì–µ–Ω–µ—Ä–∞—Ü–∏—è –∫–ª—é—á–∞ –æ–¥–∏–Ω —Ä–∞–∑ –ø—Ä–∏ —Å–±–æ—Ä–∫–µ
const ENCRYPTION_KEY = crypto.createHash('sha256')
  .update(process.env.DATA_ENCRYPTION_KEY || '')
  .digest();

export function encryptData(data: any): string {
  const iv = crypto.randomBytes(16);
  const cipher = crypto.createCipheriv('aes-256-cbc', ENCRYPTION_KEY, iv);

  let encrypted = cipher.update(JSON.stringify(data), 'utf8', 'hex');
  encrypted += cipher.final('hex');

  return `${iv.toString('hex')}:${encrypted}`;
}

export function decryptData(encryptedData: string): any {
  const [ivHex, encrypted] = encryptedData.split(':');
  const iv = Buffer.from(ivHex, 'hex');
  const decipher = crypto.createDecipheriv('aes-256-cbc', ENCRYPTION_KEY, iv);

  let decrypted = decipher.update(encrypted, 'hex', 'utf8');
  decrypted += decipher.final('utf8');

  return JSON.parse(decrypted);
}

// –ü—Ä–∏–º–µ—Ä –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è –¥–ª—è —Ö—Ä–∞–Ω–µ–Ω–∏—è —Å–µ–∫—Ä–µ—Ç–æ–≤ –≤ localStorage
export function safeStorageSet(key: string,  any) {
  if (typeof window !== 'undefined') {
    const encrypted = encryptData(data);
    localStorage.setItem(key, encrypted);
  }
}

export function safeStorageGet(key: string): any {
  if (typeof window !== 'undefined') {
    const encrypted = localStorage.getItem(key);
    return encrypted ? decryptData(encrypted) : null;
  }
  return null;
}
```

