export default {
  async fetch(request, env) {
    const url = new URL(request.url);

    // –ó–∞—â–∏—Ç–∞ AI —ç–Ω–¥–ø–æ–∏–Ω—Ç–∞
    if (url.pathname === '/api/ai-recommendations') {
