// middleware.ts
import { NextResponse } from 'next/server';
import type { NextRequest } from 'next/server';
import jwt from 'jsonwebtoken';

export async function middleware(request: NextRequest) {
  const url = request.nextUrl;

  // –ó–∞—â–∏—â–∞–µ–º –∞–¥–º–∏–Ω-–ø–∞–Ω–µ–ª—å
  if (url.pathname.startsWith('/admin')) {
    const token = request.cookies.get('admin_token')?.value;

    if (!token) {
      return NextResponse.redirect(new URL('/auth/signin', request.url));
    }

    try {
      // –í–∞–ª–∏–¥–∞—Ü–∏—è JWT —Ç–æ–∫–µ–Ω–∞
      const decoded = jwt.verify(token, process.env.JWT_SECRET!);
      const user = decoded as { githubId: string };

      // –ü—Ä–æ–≤–µ—Ä–∫–∞ –≤ –±–µ–ª–æ–º —Å–ø–∏—Å–∫–µ
      const adminIds = process.env.ADMIN_GITHUB_IDS?.split(',') || [];
      if (!adminIds.includes(user.githubId)) {
        return NextResponse.redirect(new URL('/auth/unauthorized', request.url));
      }

      // –ü—Ä–æ–≤–µ—Ä–∫–∞ Rate Limiting
      const rateLimitKey = `admin_access:${user.githubId}:${Math.floor(Date.now() / 60000)}`;
      const currentRequests = await getRateLimitCount(rateLimitKey);

      if (currentRequests > 100) { // 100 –∑–∞–ø—Ä–æ—Å–æ–≤ –≤ –º–∏–Ω—É—Ç—É
        return NextResponse.json(
          { error: '–°–ª–∏—à–∫–æ–º –º–Ω–æ–≥–æ –∑–∞–ø—Ä–æ—Å–æ–≤' }, 
          { status: 429 }
        );
      }

      await incrementRateLimit(rateLimitKey);

    } catch (error) {
      console.error('Auth middleware error:', error);
      return NextResponse.redirect(new URL('/auth/signin', request.url));
    }
  }

  return NextResponse.next();
}

// –í—Å–ø–æ–º–æ–≥–∞—Ç–µ–ª—å–Ω—ã–µ —Ñ—É–Ω–∫—Ü–∏–∏ –¥–ª—è Rate Limiting
async function getRateLimitCount(key: string): Promise<number> {
  // –ò—Å–ø–æ–ª—å–∑—É–µ–º Vercel KV –∏–ª–∏ in-memory —Ö—Ä–∞–Ω–∏–ª–∏—â–µ
  return parseInt(localStorage.getItem(key) || '0');
}

async function incrementRateLimit(key: string) {
  const current = await getRateLimitCount(key);
  localStorage.setItem(key, String(current + 1));
  // –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∞—è –æ—á–∏—Å—Ç–∫–∞ —á–µ—Ä–µ–∑ –º–∏–Ω—É—Ç—É
  setTimeout(() => localStorage.removeItem(key), 60000);
}
```

