### **10.1. –ü—Ä–∏–º–µ—Ä –ø–æ–ª–Ω–æ–≥–æ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞ –≤—Ä–µ–º–µ–Ω–Ω–æ–π —à–∫–∞–ª—ã**
```tsx
// components/timeline/Timeline.tsx
'use client';

import { useRef, useEffect, useState } from 'react';
import * as d3 from 'd3';
import { useStore } from '@/store';
import { VectorIcon } from '../ui/VectorIcons';
import { EventShape } from '../ui/ShapeComponents';

export default function Timeline() {
  const svgRef = useRef<SVGSVGElement>(null);
  const containerRef = useRef<HTMLDivElement>(null);
  const { people, events, selectedPerson, setSelectedPerson } = useStore();
  const [viewBox, setViewBox] = useState({ x: 0, y: 0, width: 800, height: 150 });
  const [isDragging, setIsDragging] = useState(false);
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });

  // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è —à–∫–∞–ª—ã
  useEffect(() => {
    if (!svgRef.current || !containerRef.current) return;

    const svg = d3.select(svgRef.current);
    const container = containerRef.current;
    const width = container.clientWidth;
    const height = 150;

    // –£—Å—Ç–∞–Ω–æ–≤–∫–∞ viewBox
    setViewBox({ x: 0, y: 0, width, height });

    // –ò—Å—Ç–æ—Ä–∏—á–µ—Å–∫–∏–µ –≥—Ä–∞–Ω–∏—Ü—ã
    const startDate = new Date(1600, 0, 1); // –°—Ç—Ä–∞–¥–∏–≤–∞—Ä–∏
    const endDate = new Date(2024, 11, 31); // –°–µ–≥–æ–¥–Ω—è

    // –ú–∞—Å—à—Ç–∞–±—ã
    const xScale = d3.scaleTime()
      .domain([startDate, endDate])
      .range([50, width - 50]);

    const yScale = d3.scaleLinear()
      .domain([0, 1])
      .range([height - 30, 30]);

    // –û—á–∏—Å—Ç–∫–∞ –ø—Ä–µ–¥—ã–¥—É—â–∏—Ö —ç–ª–µ–º–µ–Ω—Ç–æ–≤
    svg.selectAll('*').remove();

    // –õ–∏–Ω–∏—è —à–∫–∞–ª—ã
    svg.append('line')
      .attr('x1', 50)
      .attr('y1', height / 2)
      .attr('x2', width - 50)
      .attr('y2', height / 2)
      .attr('stroke', '#9ca3af')
      .attr('stroke-width', 2);

    // –î–µ–ª–µ–Ω–∏—è –Ω–∞ —à–∫–∞–ª–µ
    const ticks = xScale.ticks(d3.timeYear.every(10));
    ticks.forEach((date, i) => {
      const x = xScale(date);

      // –õ–∏–Ω–∏—è –¥–µ–ª–µ–Ω–∏—è
      svg.append('line')
        .attr('x1', x)
        .attr('y1', height / 2 - 5)
        .attr('x2', x)
        .attr('y2', height / 2 + 5)
        .attr('stroke', '#6b7280')
        .attr('stroke-width', 1);

      // –ü–æ–¥–ø–∏—Å—å –≥–æ–¥–∞
      svg.append('text')
        .attr('x', x)
        .attr('y', height / 2 + 20)
        .attr('text-anchor', 'middle')
        .attr('font-size', '10px')
        .attr('fill', '#6b7280')
        .text(date.getFullYear());
    });

    // –£–∑–ª—ã –ø–µ—Ä—Å–æ–Ω
    const randomPeople = [...people].sort(() => 0.5 - Math.random()).slice(0, 5);

    randomPeople.forEach((person, i) => {
      const birthDate = new Date(person.birth.gregorian);
      const x = xScale(birthDate);
      const y = yScale(Math.random());

      const node = svg.append('g')
        .attr('transform', `translate(${x}, ${y})`)
        .attr('class', 'timeline-node cursor-pointer hover:scale-110 transition-transform')
        .on('click', () => setSelectedPerson(person.id));

      // –ö—Ä—É–≥ —Å –∏–∫–æ–Ω–∫–æ–π
      node.append('circle')
        .attr('r', 15)
        .attr('fill', '#3b82f6')
        .attr('opacity', 0.2);

      // –í–µ–∫—Ç–æ—Ä–Ω–∞—è –∏–∫–æ–Ω–∫–∞
      node.append('foreignObject')
        .attr('width', 24)
        .attr('height', 24)
        .attr('x', -12)
        .attr('y', -12)
        .html(`
          <div class="w-full h-full flex items-center justify-center">
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
              <path stroke-linecap="round" stroke-linejoin="round" d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
            </svg>
          </div>
        `);

      // –ü–æ–¥–ø–∏—Å—å –∏–º–µ–Ω–∏
      node.append('text')
        .attr('y', 30)
        .attr('text-anchor', 'middle')
        .attr('font-size', '12px')
        .attr('fill', '#1f2937')
        .text(person.names.primary);
    });

    // –û–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ —Å–æ–±—ã—Ç–∏–π –¥–ª—è drag & zoom
    let lastX = 0;

    container.addEventListener('mousedown', (e) => {
      setIsDragging(true);
      setDragStart({ x: e.clientX, y: e.clientY });
      lastX = e.clientX;
    });

    container.addEventListener('mousemove', (e) => {
      if (!isDragging) return;

      const dx = e.clientX - lastX;
      lastX = e.clientX;

      // –°–¥–≤–∏–≥–∞–µ–º viewBox
      setViewBox(prev => ({
        ...prev,
        x: prev.x - dx
      }));
    });

    container.addEventListener('mouseup', () => {
      setIsDragging(false);
    });

    container.addEventListener('mouseleave', () => {
      if (isDragging) setIsDragging(false);
    });

    // –ó—É–º –∫–æ–ª–µ—Å–æ–º –º—ã—à–∏
    container.addEventListener('wheel', (e) => {
      e.preventDefault();
      const zoomFactor = e.deltaY > 0 ? 1.1 : 0.9;

      setViewBox(prev => {
        const mouseX = e.clientX - container.getBoundingClientRect().left;
        const relX = mouseX / width;

        const newWidth = prev.width * zoomFactor;
        const newHeight = prev.height * zoomFactor;

        const focusX = prev.x + (mouseX / width) * prev.width;
        const focusY = prev.y + (e.clientY / container.clientHeight) * prev.height;

        return {
          x: focusX - relX * newWidth,
          y: focusY - (e.clientY / container.clientHeight) * newHeight,
          width: newWidth,
          height: newHeight
        };
      });
    });

    // –û—á–∏—Å—Ç–∫–∞
    return () => {
      container.removeEventListener('mousedown', () => {});
      container.removeEventListener('mousemove', () => {});
      container.removeEventListener('mouseup', () => {});
      container.removeEventListener('mouseleave', () => {});
      container.removeEventListener('wheel', () => {});
    };
  }, [people, selectedPerson]);

  return (
    <div 
      ref={containerRef} 
      className="w-full bg-white border border-gray-200 rounded-lg p-4 shadow-sm relative overflow-hidden"
      style={{ cursor: isDragging ? 'grabbing' : 'grab' }}
    >
      <svg
        ref={svgRef}
        viewBox={`${viewBox.x} ${viewBox.y} ${viewBox.width} ${viewBox.height}`}
        className="w-full h-48 transition-transform duration-100"
      >
        {/* –≠–ª–µ–º–µ–Ω—Ç—ã –±—É–¥—É—Ç –¥–æ–±–∞–≤–ª–µ–Ω—ã —á–µ—Ä–µ–∑ D3 */}
      </svg>

      {/* –ö–Ω–æ–ø–∫–∏ —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è */}
      <div className="absolute top-2 right-2 flex space-x-2">
        <button 
          onClick={() => setViewBox(prev => ({ ...prev, width: prev.width * 0.9 }))}
          className="p-1 bg-gray-100 rounded-full hover:bg-gray-200"
          title="–ü—Ä–∏–±–ª–∏–∑–∏—Ç—å"
        >
          <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <circle cx="11" cy="11" r="8" />
            <line x1="21" y1="21" x2="16.65" y2="16.65" />
            <line x1="11" y1="8" x2="11" y2="14" />
            <line x1="8" y1="11" x2="14" y2="11" />
          </svg>
        </button>
        <button 
          onClick={() => setViewBox(prev => ({ ...prev, width: prev.width * 1.1 }))}
          className="p-1 bg-gray-100 rounded-full hover:bg-gray-200"
          title="–û—Ç–¥–∞–ª–∏—Ç—å"
        >
          <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <circle cx="11" cy="11" r="8" />
            <line x1="21" y1="21" x2="16.65" y2="16.65" />
            <line x1="11" y1="8" x2="11" y2="14" />
          </svg>
        </button>
      </div>
    </div>
  );
}
```

