import jwt from 'jsonwebtoken';
import { NextRequest, NextResponse } from 'next/server';

// –ó–∞—â–∏—Ç–∞ API —ç–Ω–¥–ø–æ–∏–Ω—Ç–æ–≤
export function withAdminProtection(handler: (req: NextRequest, res: NextResponse, user: any) => Promise<void>) {
  return async (req: NextRequest, res: NextResponse) => {
    try {
      const authHeader = req.headers.get('Authorization');
      if (!authHeader || !authHeader.startsWith('Bearer ')) {
        return NextResponse.json({ error: 'Missing authorization header' }, { status: 401 });
      }

      const token = authHeader.split(' ')[1];
      const decoded = jwt.verify(token, process.env.JWT_SECRET!);

      const user = decoded as { githubId: string };
      const adminIds = process.env.ADMIN_GITHUB_IDS?.split(',') || [];

      if (!adminIds.includes(user.githubId)) {
        return NextResponse.json({ error: 'Forbidden' }, { status: 403 });
      }

      await handler(req, res, user);

    } catch (error) {
      console.error('API Protection Error:', error);
      if (error instanceof jwt.TokenExpiredError) {
        return NextResponse.json({ error: 'Token expired' }, { status: 401 });
      }
      if (error instanceof jwt.JsonWebTokenError) {
        return NextResponse.json({ error: 'Invalid token' }, { status: 401 });
      }
      return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
    }
  };
}

// –õ–æ–≥–∏—Ä–æ–≤–∞–Ω–∏–µ –∞–∫—Ç–∏–≤–Ω–æ—Å—Ç–∏
