function generateProjectStructure() {
  const structure = [];
  const maxDepth = 3; // –û–≥—Ä–∞–Ω–∏—á–∏–≤–∞–µ–º –≥–ª—É–±–∏–Ω—É –¥–ª—è –∞–¥–∞–ø—Ç–∏–≤–Ω–æ–π –≤–µ—Ä—Å–∏–∏

  function walk(dir, prefix = '', depth = 0) {
    try {
      if (depth > maxDepth) {
        structure.push(`${prefix}‚îî‚îÄ‚îÄ [–¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–µ —Ñ–∞–π–ª—ã –∏ –ø–∞–ø–∫–∏...]`);
        return;
      }

      const items = fs.readdirSync(dir).sort();
      items.forEach((item, index) => {
        const fullPath = path.join(dir, item);
        const isLast = index === items.length - 1;
        const currentPrefix = prefix + (isLast ? '‚îî‚îÄ‚îÄ ' : '‚îú‚îÄ‚îÄ ');

        if (fs.statSync(fullPath).isDirectory()) {
          if (!['node_modules', '.git', '.next', 'logs', 'public/build'].includes(item)) {
            structure.push(`${currentPrefix}${item}/`);
            walk(fullPath, prefix + (isLast ? '    ' : '‚îÇ   '), depth + 1);
          }
        } else if (!['.DS_Store', '.gitignore', '.env', '.env.local', 'package-lock.json'].includes(item)) {
          structure.push(`${currentPrefix}${item}`);
        }
      });
    } catch (error) {
      structure.push(`${prefix}‚îî‚îÄ‚îÄ [–æ—à–∏–±–∫–∞ —á—Ç–µ–Ω–∏—è –¥–∏—Ä–µ–∫—Ç–æ—Ä–∏–∏: ${error.message}]`);
    }
  }

  walk(PROJECT_ROOT);
  return structure.join('\n');
}

