//    ,     
function isExcluded(filePath) {
  return CONFIG.scanExclude.some(pattern => {
    if (typeof pattern === 'string') {
      //       * ->   *
      const regexPattern = pattern
        .replace(/\.\*\//g, '.*\\/') // */ -> .*\/
        .replace(/\*\./g, '.*\\.')  // *. -> .*\. 
        .replace(/\*\*/g, '.*')     // ** -> .*
        .replace(/\*/g, '[^/]*');    // * -> [^/]*
      return new RegExp(`^${regexPattern}$`).test(filePath);
    }
    return pattern.test(filePath);
  });
}

//     
function getFileContentType(filePath) {
  const ext = path.extname(filePath).toLowerCase();

  if (CONFIG.fullContentTypes.some(type => filePath.endsWith(type))) {
    return 'full'; //    
  }

  if (CONFIG.briefContentTypes.some(type => filePath.endsWith(type))) {
    return 'brief'; //   
  }

  return 'auto'; //  (     )
}

//      
function getFileContent(filePath, type = 'auto') {
  try {
    const fullPath = path.join(PROJECT_ROOT, filePath);
    if (!fs.existsSync(fullPath)) {
      return `//    : ${filePath}`;
    }

    const stats = fs.statSync(fullPath);
    const fileSize = stats.size;
    const lastModified = new Date(stats.mtime).toISOString().split('T')[0];

    //     /      (    100KB)
    if (type === 'brief' || fileSize > 100 * 1024) {
      let description = '';

      //       SVG  
      if (filePath.endsWith('.svg') && type === 'full') {
        try {
          const content = fs.readFileSync(fullPath, 'utf8');
          //         SVG (   100   )
          if (content.length < 1000) {
            return content;
          }

          //      SVG  
          const lines = content.split('\n');
          const previewLines = lines.slice(0, 15);
          if (lines.length > 15) previewLines.push('  <!-- ...   -->');

          return `// SVG : ${filePath}\n// : ${Math.round(fileSize / 1024)} KB\n// : ${lastModified}\n${previewLines.join('\n')}`;
        } catch (e) {
          //   'full'  ,       SVG
          return `// SVG : ${filePath}\n// : ${Math.round(fileSize / 1024)} KB\n// : ${lastModified}\n// :\n<svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">\n  <!--      -->\n</svg>`;
        }
      }

      //        -    
      if (filePath.includes('people') || filePath.includes('events')) {
        description = '// : YAML-   ,       ';
      } else if (filePath.includes('icons')) {
        description = '// : SVG-  ,        ';
      } else if (filePath.includes('docs')) {
        description = '// :     ,      ';
      }

      return `// : ${filePath}
// : ${Math.round(fileSize / 1024)} KB
// : ${lastModified}
// : ${path.extname(filePath)}
//
${description}
//    .     .
//
//  SVG  (  ):
// <svg width="24" height="24" viewBox="0 0 24 24" fill="none" ...>
//   <!--     -->
// </svg>
`;
    }

    //      ,
    return fs.readFileSync(fullPath, 'utf8');

  } catch (error) {
    return `//     ${filePath}: ${error.message}`;
  }
}

//       
function getAllProjectFiles() {
  const files = [];

  function walk(dir, basePath = '') {
    try {
      const items = fs.readdirSync(dir);
      items.forEach(item => {
        const fullPath = path.join(dir, item);
        const relativePath = path.join(basePath, item).replace(/\\/g, '/');

        //     
        if (isExcluded(relativePath)) {
          return;
        }

        if (fs.statSync(fullPath).isDirectory()) {
          walk(fullPath, relativePath);
        } else {
          files.push(relativePath);
        }
      });
    } catch (error) {
      console.warn(`‚ö†Ô∏è     ${dir}: ${error.message}`);
    }
  }

  walk(PROJECT_ROOT);
  return files.sort(); //    
}

//       
function generateProjectStructure() {
  const structure = [];
  const maxDepth = 4; //    

  function walk(dir, prefix = '', depth = 0) {
    try {
      if (depth > maxDepth) {
        structure.push(`${prefix}‚îî‚îÄ‚îÄ [         ...]`);
        return;
      }

      const items = fs.readdirSync(dir).sort();
      items.forEach((item, index) => {
        const fullPath = path.join(dir, item);
        const relativePath = path.join(prefix.replace(/[‚îú‚îî‚îÄ‚îÄ ]/g, ''), item).replace(/\\/g, '/');

        //     
        if (isExcluded(relativePath)) {
          return;
        }

        const isLast = index === items.length - 1;
        const currentPrefix = prefix + (isLast ? '‚îî‚îÄ‚îÄ ' : '‚îú‚îÄ‚îÄ ');

        if (fs.statSync(fullPath).isDirectory()) {
          structure.push(`${currentPrefix}${item}/`);
          walk(fullPath, prefix + (isLast ? '    ' : '‚îÇ   '), depth + 1);
        } else {
          //     
          const ext = path.extname(item).toLowerCase();
          const typeIndicator = {
            '.svg': 'üñºÔ∏è',
            '.png': 'üñºÔ∏è', 
            '.jpg': 'üñºÔ∏è',
            '.json': 'üîß',
            '.yaml': 'üìã',
            '.md': 'üìù',
            '.js': 'üìú',
            '.ts': 'üìú',
            '.tsx': '‚öõÔ∏è',
            '.css': 'üé®',
            '.env': 'üîí',
            '.gitignore': '‚öôÔ∏è'
          }[ext] || 'üìÑ';

          structure.push(`${currentPrefix}${typeIndicator} ${item}`);
        }
      });
    } catch (error) {
      structure.push(`${prefix}‚îî‚îÄ‚îÄ [   : ${error.message}]`);
    }
  }

  walk(PROJECT_ROOT);
  return structure.join('\n');
}

//       
function getFilePriority(filePath) {
  for (const [level, patterns] of Object.entries(CONFIG.priorityRules)) {
    if (patterns.some(pattern => pattern.test(filePath))) {
      return level;
    }
  }
  return 'low';
}

//      
function extractFunctionSignatures(content) {
  const lines = content.split('\n');
  const signatures = [];
  let inComment = false;

  for (const line of lines) {
    //      
    if (line.includes('/*')) inComment = true;
    if (line.includes('*/')) inComment = false;
    if (inComment) continue;

    //      
    if (line.trim().startsWith('//')) continue;

    //     
    const functionMatch = line.match(/(export\s+)?(async\s+)?(function\s+)?(\w+)\s*\(/);
    const arrowMatch = line.match(/(export\s+)?(const|let|var)\s+(\w+)\s*=\s*(async\s+)?\(/);
    const classMatch = line.match(/(export\s+)?class\s+(\w+)/);

    if (functionMatch || arrowMatch || classMatch) {
      signatures.push(line.trim());
    }
  }

  return signatures.length > 0 
    ? `//   :\n${signatures.join('\n')}`
    : '//       ';
}

//        
function countLines(text) {
  return text.split('\n').length;
}

