  : ${PROJECT_ROOT}
${generateProjectStructure()}

#===============================================================================
#      ( )
#===============================================================================
`;

  let currentLines = countLines(content);

  // 1.       (60% )
  const criticalFiles = allFiles.filter(f => getFilePriority(f) === 'critical');
  criticalFiles.forEach(filePath => {
    if (currentLines < MAX_LINES * (CONFIG.adaptiveLimits.criticalPercentage / 100)) {
      const fileType = getFileContentType(filePath);
      const fileContent = getFileContent(filePath, fileType);
      const fileLines = countLines(fileContent);

      if (currentLines + fileLines < MAX_LINES * (CONFIG.adaptiveLimits.criticalPercentage / 100)) {
        const fullPath = path.join(PROJECT_ROOT, filePath);
        const lastModified = fs.existsSync(fullPath) ? new Date(fs.statSync(fullPath).mtime).toISOString().split('T')[0] : '  ';

        content += `
//--------------------------------------------------------------------------------
