// : ${lastModified}
//--------------------------------------------------------------------------------
${fileContent}
`;
        currentLines = countLines(content);
      }
    }
  });

  // 2.     (20% )
  const highPriorityFiles = allFiles.filter(f => getFilePriority(f) === 'high');
  highPriorityFiles.forEach(filePath => {
    if (currentLines < MAX_LINES * (CONFIG.adaptiveLimits.highPercentage / 100 + CONFIG.adaptiveLimits.criticalPercentage / 100)) {
      const fileType = getFileContentType(filePath);
      const fileContent = getFileContent(filePath, fileType);
      const fileLines = countLines(fileContent);

      if (currentLines + fileLines < MAX_LINES * (CONFIG.adaptiveLimits.highPercentage / 100 + CONFIG.adaptiveLimits.criticalPercentage / 100)) {
        const fullPath = path.join(PROJECT_ROOT, filePath);
        const lastModified = fs.existsSync(fullPath) ? new Date(fs.statSync(fullPath).mtime).toISOString().split('T')[0] : '  ';

        content += `
//--------------------------------------------------------------------------------
//  (  ): ${filePath}
// : ${lastModified}
//--------------------------------------------------------------------------------
${fileContent}
`;
        currentLines = countLines(content);
      }
    }
  });

  // 3.    (10% )
  if (currentLines < MAX_LINES * 0.9) {
    const mediumPriorityFiles = allFiles.filter(f => getFilePriority(f) === 'medium');
    mediumPriorityFiles.forEach(filePath => {
      if (currentLines < MAX_LINES * 0.9) {
        const fileType = getFileContentType(filePath);
        let contentToAdd;

        if (fileType === 'full') {
          const fileContent = getFileContent(filePath);
          contentToAdd = extractFunctionSignatures(fileContent);
        } else {
          contentToAdd = getFileContent(filePath, 'brief');
        }

        const sigLines = countLines(contentToAdd);

        if (currentLines + sigLines < MAX_LINES * 0.9) {
          const fullPath = path.join(PROJECT_ROOT, filePath);
          const lastModified = fs.existsSync(fullPath) ? new Date(fs.statSync(fullPath).mtime).toISOString().split('T')[0] : '  ';

          content += `
//--------------------------------------------------------------------------------
//  (  -  ): ${filePath}
// : ${lastModified}
//      :   
//--------------------------------------------------------------------------------
${contentToAdd}
`;
          currentLines = countLines(content);
        }
      }
    });
  }

  // 4.     (    )
  if (currentLines < MAX_LINES) {
    const lowPriorityFiles = allFiles.filter(f => getFilePriority(f) === 'low');
    if (lowPriorityFiles.length > 0) {
      content += `
//===============================================================================
#   (  )
#        project-full.txt
#===============================================================================
`;

      lowPriorityFiles.forEach(filePath => {
        content += `// ${filePath}\n`;
      });

      currentLines = countLines(content);
    }
  }

  // 5.    (   )
  if (currentLines < MAX_LINES) {
    const guidelines = generateAIGuidelines();
    //    ,      
    if (currentLines + countLines(guidelines) > MAX_LINES) {
      const briefGuidelines = `
#===============================================================================
#   (  )
#===============================================================================

## üö®  
1.      -          ,
2.  :
    : / /.tsx
