// –ü–æ–ª—É—á–∞–µ–º –≤—Å–µ —Ñ–∞–π–ª—ã –ø—Ä–æ–µ–∫—Ç–∞
function getAllProjectFiles() {
  const files = [];

  function walk(dir, basePath = '') {
    try {
      const items = fs.readdirSync(dir);
      items.forEach(item => {
        const fullPath = path.join(dir, item);
        const relativePath = path.join(basePath, item).replace(/\\/g, '/');

        // –ü—Ä–æ–ø—É—Å–∫–∞–µ–º –∏—Å–∫–ª—é—á–µ–Ω–Ω—ã–µ —Ñ–∞–π–ª—ã
        if (isExcluded(relativePath)) {
          return;
        }

        if (fs.statSync(fullPath).isDirectory()) {
          walk(fullPath, relativePath);
        } else {
          files.push(relativePath);
        }
      });
    } catch (error) {
      console.warn(`‚ö†Ô∏è –ù–µ —É–¥–∞–ª–æ—Å—å –ø—Ä–æ—á–∏—Ç–∞—Ç—å –¥–∏—Ä–µ–∫—Ç–æ—Ä–∏—é ${dir}: ${error.message}`);
    }
  }

  walk(PROJECT_ROOT);
  return files.sort(); // –°–æ—Ä—Ç–∏—Ä—É–µ–º –¥–ª—è –∫–æ–Ω—Å–∏—Å—Ç–µ–Ω—Ç–Ω–æ—Å—Ç–∏
}

// –ì–µ–Ω–µ—Ä–∏—Ä—É–µ–º —Å—Ç—Ä—É–∫—Ç—É—Ä—É –ø—Ä–æ–µ–∫—Ç–∞
function generateProjectStructure() {
  const structure = [];
  const maxDepth = 4; // –£–≤–µ–ª–∏—á–∏–≤–∞–µ–º –≥–ª—É–±–∏–Ω—É –¥–ª—è –ø–æ–ª–Ω–æ–π —Å—Ç—Ä—É–∫—Ç—É—Ä—ã

  function walk(dir, prefix = '', depth = 0) {
    try {
      if (depth > maxDepth) {
        structure.push(`${prefix}‚îî‚îÄ‚îÄ [–¥–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–µ —Ñ–∞–π–ª—ã –∏ –ø–∞–ø–∫–∏...]`);
        return;
      }

      const items = fs.readdirSync(dir).sort();
      items.forEach((item, index) => {
        const fullPath = path.join(dir, item);
        const relativePath = path.join(prefix.replace(/[‚îú‚îî‚îÄ‚îÄ ]/g, ''), item).replace(/\\/g, '/');

        // –ü—Ä–æ–ø—É—Å–∫–∞–µ–º –∏—Å–∫–ª—é—á–µ–Ω–Ω—ã–µ —Ñ–∞–π–ª—ã
        if (isExcluded(relativePath)) {
          return;
        }

        const isLast = index === items.length - 1;
        const currentPrefix = prefix + (isLast ? '‚îî‚îÄ‚îÄ ' : '‚îú‚îÄ‚îÄ ');

        if (fs.statSync(fullPath).isDirectory()) {
          structure.push(`${currentPrefix}${item}/`);
          walk(fullPath, prefix + (isLast ? '    ' : '‚îÇ   '), depth + 1);
        } else {
          // –û—Ç–º–µ—á–∞–µ–º —Ç–∏–ø —Ñ–∞–π–ª–∞ –¥–ª—è –Ω–∞–≥–ª—è–¥–Ω–æ—Å—Ç–∏
          const ext = path.extname(item).toLowerCase();
          const typeIndicator = {
            '.svg': 'üñºÔ∏è',
            '.png': 'üñºÔ∏è', 
            '.jpg': 'üñºÔ∏è',
            '.json': 'üîß',
            '.yaml': 'üìã',
            '.md': 'üìù',
            '.js': 'üìú',
            '.ts': 'üìú',
            '.tsx': '‚öõÔ∏è',
            '.css': 'üé®',
            '.env': 'üîí',
            '.gitignore': '‚öôÔ∏è'
          }[ext] || 'üìÑ';

          structure.push(`${currentPrefix}${typeIndicator} ${item}`);
        }
      });
    } catch (error) {
      structure.push(`${prefix}‚îî‚îÄ‚îÄ [–æ—à–∏–±–∫–∞ —á—Ç–µ–Ω–∏—è –¥–∏—Ä–µ–∫—Ç–æ—Ä–∏–∏: ${error.message}]`);
    }
  }

  walk(PROJECT_ROOT);
  return structure.join('\n');
}

// –û–ø—Ä–µ–¥–µ–ª—è–µ–º –ø—Ä–∏–æ—Ä–∏—Ç–µ—Ç —Ñ–∞–π–ª–∞
function getFilePriority(filePath) {
  for (const [level, patterns] of Object.entries(CONFIG.priorityRules)) {
    if (patterns.some(pattern => pattern.test(filePath))) {
      return level;
    }
  }
  return 'low';
}

// –ò–∑–≤–ª–µ–∫–∞–µ–º —Å–∏–≥–Ω–∞—Ç—É—Ä—ã —Ñ—É–Ω–∫—Ü–∏–π –¥–ª—è —Å–æ–∫—Ä–∞—â–µ–Ω–Ω–æ–π –≤–µ—Ä—Å–∏–∏
function extractFunctionSignatures(content) {
  const lines = content.split('\n');
  const signatures = [];
  let inComment = false;

  for (const line of lines) {
    // –ü—Ä–æ–ø—É—Å–∫–∞–µ–º –º–Ω–æ–≥–æ—Å—Ç—Ä–æ—á–Ω—ã–µ –∫–æ–º–º–µ–Ω—Ç–∞—Ä–∏–∏
    if (line.includes('/*')) inComment = true;
    if (line.includes('*/')) inComment = false;
    if (inComment) continue;

    // –ü—Ä–æ–ø—É—Å–∫–∞–µ–º –æ–¥–Ω–æ—Å—Ç—Ä–æ—á–Ω—ã–µ –∫–æ–º–º–µ–Ω—Ç–∞—Ä–∏–∏
    if (line.trim().startsWith('//')) continue;

    // –ò—â–µ–º –æ–±—ä—è–≤–ª–µ–Ω–∏—è —Ñ—É–Ω–∫—Ü–∏–π
    const functionMatch = line.match(/(export\s+)?(async\s+)?(function\s+)?(\w+)\s*\(/);
    const arrowMatch = line.match(/(export\s+)?(const|let|var)\s+(\w+)\s*=\s*(async\s+)?\(/);
    const classMatch = line.match(/(export\s+)?class\s+(\w+)/);

    if (functionMatch || arrowMatch || classMatch) {
      signatures.push(line.trim());
    }
  }

  return signatures.length > 0 
    ? `// –°–∏–≥–Ω–∞—Ç—É—Ä—ã —Ñ—É–Ω–∫—Ü–∏–π:\n${signatures.join('\n')}`
    : '// –ù–µ—Ç —Ñ—É–Ω–∫—Ü–∏–π –¥–ª—è –∏–∑–≤–ª–µ—á–µ–Ω–∏—è';
}

