// –§—É–Ω–∫—Ü–∏—è –ø—Ä–æ–≤–µ—Ä–∫–∏, –∏—Å–∫–ª—é—á–µ–Ω –ª–∏ —Ñ–∞–π–ª
function isExcluded(filePath) {
  return CONFIG.scanExclude.some(pattern => {
    if (typeof pattern === 'string') {
      const regexPattern = pattern
        .replace(/\.\*\//g, '.*\\/')
        .replace(/\*\./g, '.*\\.')
        .replace(/\*\*/g, '.*')
        .replace(/\*/g, '[^/]*');
      return new RegExp(`^${regexPattern}$`).test(filePath.replace(/\\/g, '/'));
    }
    return pattern.test(filePath.replace(/\\/g, '/'));
  });
}

// –§—É–Ω–∫—Ü–∏—è –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–∏—è —Ç–∏–ø–∞ —Å–æ–¥–µ—Ä–∂–∏–º–æ–≥–æ —Ñ–∞–π–ª–∞
function getFileContentType(filePath) {
  const ext = path.extname(filePath).toLowerCase();

  if (CONFIG.fullContentTypes.some(type => filePath.endsWith(type))) {
    return 'full';
  }

  if (CONFIG.briefContentTypes.some(type => filePath.endsWith(type))) {
    return 'brief';
  }

  return 'auto';
}

