// components/graph/ConnectionGraph.tsx
import { VectorIcon } from '../ui/VectorIcons';
import { ConnectionLine } from '../ui/ShapeComponents';

const CONNECTION_STYLES = {
  teacher: { color: '#2563eb', icon: 'FaUserGraduate' },
  concert: { color: '#8b5cf6', icon: 'FaUsers' },
  relative: { color: '#ef4444', icon: 'FaHeart' },
  instrument: { color: '#16a34a', icon: 'FaTools' },
  friend: { color: '#f59e0b', icon: 'FaHandshake' }
};

export function ConnectionGraph({ data, selectedNodeId }: {  any; selectedNodeId: string | null }) {
  const [highlightedNodes, setHighlightedNodes] = useState<string[]>([]);

  const handleNodeClick = (nodeId: string) => {
    setSelectedNode(nodeId);

    // Find related nodes
    const relatedNodes = data.connections
      .filter(conn => conn.source === nodeId || conn.target === nodeId)
      .map(conn => conn.source === nodeId ? conn.target : conn.source);

    setHighlightedNodes([...relatedNodes, nodeId]);
  };

  return (
    <div className="relative w-full h-[600px] bg-gray-50">
      {data.connections.map((conn: any) => (
        <ConnectionLine
          key={`${conn.source}-${conn.target}`}
          start={{ x: getNodeX(conn.source), y: getNodeY(conn.source) }}
          end={{ x: getNodeX(conn.target), y: getNodeY(conn.target) }}
          type={conn.type}
          highlighted={highlightedNodes.includes(conn.source) || highlightedNodes.includes(conn.target)}
        />
      ))}

      {data.people.map((person: any) => (
        <div 
          key={person.id}
          className={`absolute transform -translate-x-1/2 -translate-y-1/2 cursor-pointer transition-all duration-300 ${
            highlightedNodes.includes(person.id) 
              ? 'scale-125 z-10' 
              : 'scale-100 z-0'
          }`}
          style={{ 
            left: `${getNodeX(person.id)}px`, 
            top: `${getNodeY(person.id)}px` 
          }}
          onClick={() => handleNodeClick(person.id)}
        >
          <div className={`w-12 h-12 rounded-full flex items-center justify-center ${
            selectedNodeId === person.id 
              ? 'bg-blue-500 text-white shadow-lg' 
              : 'bg-white shadow-md'
          }`}>
            <VectorIcon type="violinist" size={24} className="text-blue-600" />
          </div>
          <div className="mt-1 text-xs text-center font-medium">{person.names.primary}</div>
        </div>
      ))}
    </div>
  );
}
```

## 3. ADMIN PANEL WITH YANDEX GPT INTEGRATION
- Authentication via GitHub OAuth
- AI recommendations panel with automatic suggestions
- Data editor with validation
- Security audit logs
- Context-aware code generation instructions

### Technical Implementation
```typescript
// app/api/ai-analysis/route.ts
import { NextResponse } from 'next/server';
import { withAdminProtection } from '@/lib/security';
import { getAIRecommendations } from '@/lib/ai-analysis';

export const POST = withAdminProtection(async (req: Request, res: NextResponse, user: any) => {
  try {
    const { data } = await req.json();

    // Rate limiting
    const rateLimitKey = `ai_requests:${user.githubId}:${Math.floor(Date.now() / 60000)}`;
    const currentRequests = await getRateLimitCount(rateLimitKey);

    if (currentRequests >= 10) { // 10 requests per minute
      return NextResponse.json(
        { error: 'Too many requests', retry_after: 60 - (Date.now() % 60000) / 1000 }, 
        { status: 429 }
      );
    }

    await incrementRateLimit(rateLimitKey);

    // Get AI recommendations
    const recommendations = await getAIRecommendations(data);

    // Log activity
    logActivity('ai_analysis', { 
      userId: user.githubId, 
      itemsAnalyzed: data.people.length, 
      recommendations: recommendations.new_events.length 
    });

    return NextResponse.json(recommendations);

  } catch (error) {
    console.error('AI API Error:', error);
    logError('ai_analysis', error);
    return NextResponse.json({ error: 'AI analysis failed' }, { status: 500 });
  }
});
```

