export async function sendTelegramMessage(text: string) {
  const botToken = process.env.TELEGRAM_BOT_TOKEN!;
  const chatId = process.env.TELEGRAM_CHAT_ID!;

  const response = await fetch(
    `https://api.telegram.org/bot${botToken}/sendMessage`,
    {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ 
        chat_id: chatId, 
        text, 
        parse_mode: 'HTML',
        disable_web_page_preview: true
      }),
    }
  );

  if (!response.ok) {
    const errorData = await response.json();
    throw new Error(`Telegram API error: ${errorData.description}`);
  }

  return response.json();
}

// app/api/feedback/route.ts
export async function POST(request: NextRequest) {
  try {
    const { name, email, message, contactMethod } = await request.json();

    // Validation
    if (!name || !message) {
      return NextResponse.json(
        { error: 'Name and message are required' }, 
        { status: 400 }
      );
    }

    // Send to Telegram
    const telegramMessage = `
üîî New feedback message

üë§ Name: ${name}
üìß Email: ${email || 'not provided'}
üì± Contact method: ${contactMethod || 'not specified'}

üí¨ Message:
${message}
    `;

    await sendTelegramMessage(telegramMessage);

    // Send confirmation if requested
    if (contactMethod === 'email' && email) {
      await sendEmailConfirmation(email, name, message);
    } else if (contactMethod === 'telegram') {
      await sendTelegramConfirmation(name);
    }

    return NextResponse.json({ success: true });
  } catch (error) {
    console.error('Feedback error:', error);
    return NextResponse.json(
      { error: 'Failed to send message' }, 
      { status: 500 }
    );
  }
}
