```typescript
// lib/pdf-export.ts
import { PDFDocument, StandardFonts, rgb } from 'pdf-lib';
import fontkit from '@pdf-lib/fontkit';

export async function exportLifeLinesToPDF(people: any[], events: any[]) {
  const pdfDoc = await PDFDocument.create();
  pdfDoc.registerFontkit(fontkit);

  // Add font
  const fontBytes = await fetch('/fonts/Roboto-Regular.ttf').then(res => res.arrayBuffer());
  const customFont = await pdfDoc.embedFont(fontBytes);

  const page = pdfDoc.addPage();
  const { width, height } = page.getSize();

  // Header
  page.drawText('Life Paths of Violinists', {
    x: 50,
    y: height - 50,
    size: 24,
    font: customFont,
    color: rgb(0.1, 0.1, 0.1),
  });

  // Page content
  let yPos = height - 100;
  for (const person of people) {
    // Person name and lifespan
    page.drawText(`${person.names.primary} (${person.birth.display} - ${person.death?.display || 'present'})`, {
      x: 50,
      y: yPos,
      size: 16,
      font: customFont,
      color: rgb(0.2, 0.4, 0.8),
    });

    yPos -= 25;

    // Events
    for (const eventId of person.events) {
      const event = events.find(e => e.id === eventId);
      if (event) {
        page.drawText(`‚Ä¢ ${event.date.display}: ${event.title.primary}`, {
          x: 70,
          y: yPos,
          size: 12,
          font: customFont,
          color: rgb(0.3, 0.3, 0.3),
        });
        yPos -= 20;
      }
    }

    yPos -= 10;

    // Break to new page if needed
    if (yPos < 100) {
      page.drawText('Page ' + pdfDoc.getPages().length, {
        x: width - 60,
        y: 30,
        size: 10,
        font: customFont,
        color: rgb(0.6, 0.6, 0.6),
      });

      page = pdfDoc.addPage();
      yPos = height - 70;
    }
  }

  // Footer on last page
  page.drawText(`Exported: ${new Date().toLocaleDateString()}`, {
    x: 50,
    y: 30,
    size: 10,
    font: customFont,
    color: rgb(0.6, 0.6, 0.6),
  });
  page.drawText(`Page ${pdfDoc.getPages().length}`, {
    x: width - 60,
    y: 30,
    size: 10,
    font: customFont,
    color: rgb(0.6, 0.6, 0.6),
  });

  return await pdfDoc.save();
}
