// components/graph/ConnectionGraph.tsx
import { useTranslations } from 'next-intl';
import { VectorIcon } from '../ui/VectorIcons';
import { ConnectionLine } from '../ui/ShapeComponents';

const CONNECTION_STYLES = {
  teacher: { color: '#2563eb', icon: 'teacher' },
  concert: { color: '#8b5cf6', icon: 'concert' },
  relative: { color: '#ef4444', icon: 'relative' },
  instrument: { color: '#16a34a', icon: 'instrument' },
  friend: { color: '#f59e0b', icon: 'friend' }
};

export function ConnectionGraph({ data, selectedNodeId }: {  any; selectedNodeId: string | null }) {
  const t = useTranslations('graph');
  const [highlightedNodes, setHighlightedNodes] = useState<string[]>([]);

  const handleNodeClick = (nodeId: string) => {
    setSelectedNode(nodeId);

    // –ü–æ–∏—Å–∫ —Å–≤—è–∑–∞–Ω–Ω—ã—Ö —É–∑–ª–æ–≤
    const relatedNodes = data.connections
      .filter(conn => conn.source === nodeId || conn.target === nodeId)
      .map(conn => conn.source === nodeId ? conn.target : conn.source);

    setHighlightedNodes([...relatedNodes, nodeId]);
  };

  return (
    <div className="relative w-full h-[600px] bg-gray-50">
      {data.connections.map((conn: any) => (
        <ConnectionLine
          key={`${conn.source}-${conn.target}`}
          start={{ x: getNodeX(conn.source), y: getNodeY(conn.source) }}
          end={{ x: getNodeX(conn.target), y: getNodeY(conn.target) }}
          type={conn.type}
          highlighted={highlightedNodes.includes(conn.source) || highlightedNodes.includes(conn.target)}
        />
      ))}

      {data.people.map((person: any) => (
        <div 
          key={person.id}
          className={`absolute transform -translate-x-1/2 -translate-y-1/2 cursor-pointer transition-all duration-300 ${
            highlightedNodes.includes(person.id) 
              ? 'scale-125 z-10' 
              : 'scale-100 z-0'
          }`}
          style={{ 
            left: `${getNodeX(person.id)}px`, 
            top: `${getNodeY(person.id)}px` 
          }}
          onClick={() => handleNodeClick(person.id)}
          title={`${person.names.primary} (${t(person.bio.short)})`}
        >
          <div className={`w-12 h-12 rounded-full flex items-center justify-center ${
            selectedNodeId === person.id 
              ? 'bg-blue-500 text-white shadow-lg' 
              : 'bg-white shadow-md'
          }`}>
            <VectorIcon type="violinist" size={24} className="text-blue-600" />
          </div>
          <div className="mt-1 text-xs text-center font-medium">{person.names.primary}</div>
        </div>
      ))}
    </div>
  );
}
```

