// app/api/ai-analysis/route.ts
import { NextResponse } from 'next/server';
import { withAdminProtection } from '@/lib/security';
import { getAIRecommendations } from '@/lib/ai-analysis';
import { t } from '@/lib/i18n';

export const POST = withAdminProtection(async (req: Request, res: NextResponse, user: any) => {
  try {
    const { data, locale } = await req.json();

    // –û–≥—Ä–∞–Ω–∏—á–µ–Ω–∏–µ –∫–æ–ª–∏—á–µ—Å—Ç–≤–∞ –∑–∞–ø—Ä–æ—Å–æ–≤
    const rateLimitKey = `ai_requests:${user.githubId}:${Math.floor(Date.now() / 60000)}`;
    const currentRequests = await getRateLimitCount(rateLimitKey);

    if (currentRequests >= 10) { // 10 –∑–∞–ø—Ä–æ—Å–æ–≤ –≤ –º–∏–Ω—É—Ç—É
      return NextResponse.json(
        { 
          error: t('admin.tooManyRequests', locale),
          retry_after: 60 - (Date.now() % 60000) / 1000 
        }, 
        { status: 429 }
      );
    }

    await incrementRateLimit(rateLimitKey);

    // –ü–æ–ª—É—á–µ–Ω–∏–µ —Ä–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–π –æ—Ç –ò–ò
    const recommendations = await getAIRecommendations(data, locale);

    // –õ–æ–≥–∏—Ä–æ–≤–∞–Ω–∏–µ –∞–∫—Ç–∏–≤–Ω–æ—Å—Ç–∏
    logActivity('ai_analysis', { 
      userId: user.githubId, 
      itemsAnalyzed: data.people.length, 
      recommendations: recommendations.new_events.length,
      locale
    });

    return NextResponse.json(recommendations);

  } catch (error) {
    console.error('AI API Error:', error);
    logError('ai_analysis', error);
    return NextResponse.json({ error: t('admin.aiFailed', locale) }, { status: 500 });
  }
});
