// lib/telegram.ts
export async function sendTelegramMessage(text: string, locale: string) {
  const botToken = process.env.TELEGRAM_BOT_TOKEN!;
  const chatId = process.env.TELEGRAM_CHAT_ID!;

  // –õ–æ–∫–∞–ª–∏–∑–∞—Ü–∏—è —Å–æ–æ–±—â–µ–Ω–∏—è
  const localizedText = locale === 'ru' 
    ? `üîî –ù–æ–≤–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ –æ–±—Ä–∞—Ç–Ω–æ–π —Å–≤—è–∑–∏\n\n${text}`
    : `üîî New feedback message\n\n${text}`;

  const response = await fetch(
    `https://api.telegram.org/bot${botToken}/sendMessage`,
    {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ 
        chat_id: chatId, 
        text: localizedText, 
        parse_mode: 'HTML',
        disable_web_page_preview: true
      }),
    }
  );

  if (!response.ok) {
    const errorData = await response.json();
    throw new Error(`Telegram API error: ${errorData.description}`);
  }

  return response.json();
}

// app/api/feedback/route.ts
export async function POST(request: NextRequest) {
  try {
    const { name, email, message, contactMethod, locale } = await request.json();

    // –í–∞–ª–∏–¥–∞—Ü–∏—è
    if (!name || !message) {
      return NextResponse.json(
        { error: t('feedback.requiredFields', locale) }, 
        { status: 400 }
      );
    }

    // –û—Ç–ø—Ä–∞–≤–∫–∞ –≤ Telegram
    const telegramMessage = `
üë§ ${t('feedback.name', locale)}: ${name}
üìß ${t('feedback.email', locale)}: ${email || t('feedback.notProvided', locale)}
üì± ${t('feedback.contactMethod', locale)}: ${contactMethod || t('feedback.notSpecified', locale)}

üí¨ ${t('feedback.message', locale)}:
${message}
    `;

    await sendTelegramMessage(telegramMessage, locale);

    // –û—Ç–ø—Ä–∞–≤–∫–∞ –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏—è
    if (contactMethod === 'email' && email) {
      await sendEmailConfirmation(email, name, locale);
    } else if (contactMethod === 'telegram') {
      await sendTelegramConfirmation(name, locale);
    }

    return NextResponse.json({ 
      success: true,
      message: t('feedback.success', locale)
    });
  } catch (error) {
    console.error('Feedback error:', error);
    return NextResponse.json(
      { error: t('feedback.failed', locale) }, 
      { status: 500 }
    );
  }
}
```

