    fontBytes = await fetch('/fonts/Amiri-Regular.ttf').then(res => res.arrayBuffer());
  } else {
    fontBytes = await fetch('/fonts/Roboto-Regular.ttf').then(res => res.arrayBuffer());
  }

  const customFont = await pdfDoc.embedFont(fontBytes);

  const page = pdfDoc.addPage();
  const { width, height } = page.getSize();

  // –ó–∞–≥–æ–ª–æ–≤–æ–∫
  page.drawText(t('pdf.title', locale), {
    x: 50,
    y: height - 50,
    size: 24,
    font: customFont,
    color: rgb(0.1, 0.1, 0.1),
  });

  // –û—Å–Ω–æ–≤–Ω–æ–µ —Å–æ–¥–µ—Ä–∂–∏–º–æ–µ
  let yPos = height - 100;
  for (const person of people) {
    // –ò–º—è –ø–µ—Ä—Å–æ–Ω—ã –∏ –ø–µ—Ä–∏–æ–¥ –∂–∏–∑–Ω–∏
    page.drawText(`${person.names.primary} (${person.birth.display} - ${person.death?.display || t('pdf.present', locale)})`, {
      x: 50,
      y: yPos,
      size: 16,
      font: customFont,
      color: rgb(0.2, 0.4, 0.8),
    });

    yPos -= 25;

    // –°–æ–±—ã—Ç–∏—è
    for (const eventId of person.events) {
      const event = events.find(e => e.id === eventId);
      if (event) {
        page.drawText(`‚Ä¢ ${event.date.display}: ${event.title.primary}`, {
          x: 70,
          y: yPos,
          size: 12,
          font: customFont,
          color: rgb(0.3, 0.3, 0.3),
        });
        yPos -= 20;
      }
    }

    yPos -= 10;

    // –ü–µ—Ä–µ—Ö–æ–¥ –Ω–∞ –Ω–æ–≤—É—é —Å—Ç—Ä–∞–Ω–∏—Ü—É –ø—Ä–∏ –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ—Å—Ç–∏
    if (yPos < 100) {
      page.drawText(t('pdf.page', locale) + ' ' + pdfDoc.getPages().length, {
        x: width - 60,
        y: 30,
        size: 10,
        font: customFont,
        color: rgb(0.6, 0.6, 0.6),
      });

      page = pdfDoc.addPage();
      yPos = height - 70;
    }
  }

  // –§—É—Ç–µ—Ä –Ω–∞ –ø–æ—Å–ª–µ–¥–Ω–µ–π —Å—Ç—Ä–∞–Ω–∏—Ü–µ
  page.drawText(t('pdf.exported', locale) + ': ' + new Date().toLocaleDateString(locale), {
    x: 50,
    y: 30,
    size: 10,
    font: customFont,
    color: rgb(0.6, 0.6, 0.6),
  });
  page.drawText(t('pdf.page', locale) + ' ' + pdfDoc.getPages().length, {
    x: width - 60,
    y: 30,
    size: 10,
    font: customFont,
    color: rgb(0.6, 0.6, 0.6),
  });

  return await pdfDoc.save();
}
```

