"use client";
import { useRef, useEffect } from "react";
import * as d3 from "d3";
import { useStore } from "@/store";

export default function Timeline() {
  const svgRef = useRef<SVGSVGElement>(null);
  const { selectedPerson, setSelectedPerson } = useStore();

  useEffect(() => {
    if (!svgRef.current) return;

    const width = svgRef.current.clientWidth;
    const height = 80;
    const svg = d3.select(svgRef.current);

    // –ò—Å—Ç–æ—Ä–∏—á–µ—Å–∫–∏–µ –≥—Ä–∞–Ω–∏—Ü—ã
    const startDate = new Date(1600, 0, 1); // –°—Ç—Ä–∞–¥–∏–≤–∞—Ä–∏
    const endDate = new Date(); // –°–µ–≥–æ–¥–Ω—è

    // –ú–∞—Å—à—Ç–∞–± –¥–ª—è –≤—Ä–µ–º–µ–Ω–Ω–æ–π —à–∫–∞–ª—ã
    const xScale = d3.scaleTime()
      .domain([startDate, endDate])
      .range([0, width]);

    // –ó—É–º
    const zoom = d3.zoom<SVGSVGElement, unknown>()
      .scaleExtent([1, 1000])
      .on("zoom", (event) => {
        const newX = event.transform.rescaleX(xScale);
        xScale.domain(newX.domain());
        renderAxis();
      });

    const renderAxis = () => {
      svg.select(".x-axis").remove();
      svg.append("g")
        .attr("class", "x-axis")
        .attr("transform", `translate(0, ${height - 20})`)
        .call(d3.axisBottom(xScale).ticks(10));
    };

    // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è
    svg.call(zoom);
    renderAxis();

    // –£–∑–ª—ã –ø–µ—Ä—Å–æ–Ω
    if (selectedPerson) {
      // –ü–æ–¥—Å–≤–µ—Ç–∫–∞ —Å–≤—è–∑–∞–Ω–Ω—ã—Ö —Å–æ–±—ã—Ç–∏–π
      const relatedEvents = data.people.find(p => p.id === selectedPerson)?.events || [];
      relatedEvents.forEach(eventId => {
        const event = data.events.find(e => e.id === eventId);
        const xPos = xScale(new Date(event.date.gregorian));
        svg.append("circle")
          .attr("cx", xPos)
          .attr("cy", height / 2)
          .attr("r", 8)
          .attr("fill", "rgba(99, 102, 241, 0.7)")
          .on("click", () => setSelectedEvent(eventId));
      });
    }
  }, [selectedPerson]);

  return (
    <svg ref={svgRef} width="100%" height="80" className="border-b border-gray-200">
      {/* –î–∏–Ω–∞–º–∏—á–µ—Å–∫–∏–µ —ç–ª–µ–º–µ–Ω—Ç—ã –±—É–¥—É—Ç –¥–æ–±–∞–≤–ª–µ–Ω—ã —á–µ—Ä–µ–∑ D3 */}
    </svg>
  );
}
```

