import { NextApiRequest, NextApiResponse } from 'next';
import { YandexGPT } from '@yandex-cloud/nodejs-sdk';

const IAM_TOKEN = process.env.YANDEX_GPT_IAM_TOKEN!;
const FOLDER_ID = process.env.YANDEX_GPT_FOLDER_ID!;

export default async function handler(
  req: NextApiRequest,
  res: NextApiResponse
) {
  // –ó–∞—â–∏—Ç–∞ —ç–Ω–¥–ø–æ–∏–Ω—Ç–∞ (—Ç–æ–ª—å–∫–æ –¥–ª—è –∞–¥–º–∏–Ω–∞)
  if (req.headers.authorization !== `Bearer ${process.env.ADMIN_SECRET}`) {
    return res.status(401).json({ error: 'Unauthorized' });
  }

  try {
    const { data } = req.body;

    // –§–æ—Ä–º–∏—Ä—É–µ–º –ø—Ä–æ–º–ø—Ç –¥–ª—è –∞–Ω–∞–ª–∏–∑–∞
    const prompt = `
