// lib/ai-cache.ts
const CACHE_DURATION = 30 * 60 * 1000; // 30 –º–∏–Ω—É—Ç

export function getCachedRecommendations(key: string) {
  const cached = localStorage.getItem(`ai_cache_${key}`);
  if (!cached) return null;

  const { timestamp, data } = JSON.parse(cached);
  if (Date.now() - timestamp > CACHE_DURATION) {
    localStorage.removeItem(`ai_cache_${key}`);
    return null;
  }

  return data;
}

export function setCachedRecommendations(key: string,  any) {
  localStorage.setItem(`ai_cache_${key}`, JSON.stringify({
