// lib/encryption.ts
import crypto from 'crypto';

export class DataEncryptor {
  private static readonly ALGORITHM = 'aes-256-gcm';
  private static readonly KEY_LENGTH = 32;
  private static readonly IV_LENGTH = 16;
  private static readonly AUTH_TAG_LENGTH = 16;

  private key: Buffer;

  constructor() {
    this.key = crypto.createHash('sha256')
      .update(process.env.DATA_ENCRYPTION_KEY || '')
      .digest();
  }

  encrypt( any): string {
    const iv = crypto.randomBytes(DataEncryptor.IV_LENGTH);
    const cipher = crypto.createCipheriv(DataEncryptor.ALGORITHM, this.key, iv);

    const encrypted = cipher.update(JSON.stringify(data), 'utf8', 'hex');
    const final = cipher.final('hex');
    const authTag = cipher.getAuthTag();

    return `${iv.toString('hex')}:${authTag.toString('hex')}:${encrypted}${final}`;
  }

  decrypt(encryptedData: string): any {
    const [ivHex, authTagHex, encrypted] = encryptedData.split(':');
    const iv = Buffer.from(ivHex, 'hex');
    const authTag = Buffer.from(authTagHex, 'hex');

    const decipher = crypto.createDecipheriv(DataEncryptor.ALGORITHM, this.key, iv);
    decipher.setAuthTag(authTag);

    const decrypted = decipher.update(encrypted, 'hex', 'utf8');
    const final = decipher.final('utf8');

    return JSON.parse(decrypted + final);
  }

  static async encryptBackup(data: Buffer): Promise<Buffer> {
    const key = crypto.randomBytes(32);
    const iv = crypto.randomBytes(16);

    const cipher = crypto.createCipheriv('aes-256-cbc', key, iv);
    let encrypted = cipher.update(data);
    encrypted = Buffer.concat([encrypted, cipher.final()]);

    return Buffer.concat([iv, encrypted]);
  }
}
