//    ,     
function isExcluded(filePath) {
  return CONFIG.scanExclude.some(pattern => {
    if (typeof pattern === 'string') {
      //       * ->   *
      const regexPattern = pattern
        .replace(/\.\*\//g, '.*\\/') // */ -> .*\/
        .replace(/\*\./g, '.*\\.')  // *. -> .*\. 
        .replace(/\*\*/g, '.*')     // ** -> .*
        .replace(/\*/g, '[^/]*');    // * -> [^/]*
      return new RegExp(`^${regexPattern}$`).test(filePath);
    }
    return pattern.test(filePath);
  });
}

