    return 'full'; // –ü–æ–ª–Ω–æ–µ —Å–æ–¥–µ—Ä–∂–∏–º–æ–µ
  }

  if (CONFIG.briefContentTypes.some(type => filePath.endsWith(type))) {
    return 'brief'; // –ö—Ä–∞—Ç–∫–æ–µ –æ–ø–∏—Å–∞–Ω–∏–µ (–º–µ—Ç–∞–¥–∞–Ω–Ω—ã–µ)
  }

  return 'auto'; // –ê–≤—Ç–æ–æ–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ
}

// –ü–æ–ª—É—á–∞–µ–º —Å–æ–¥–µ—Ä–∂–∏–º–æ–µ —Ñ–∞–π–ª–∞ —Å —É—á–µ—Ç–æ–º —Ç–∏–ø–∞
function getFileContent(filePath, type = 'auto') {
