```typescript
// lib/i18n.ts
import { getRequestConfig } from 'next-intl/server';
import { notFound } from 'next/navigation';

// –°–ø–∏—Å–æ–∫ –ø–æ–¥–¥–µ—Ä–∂–∏–≤–∞–µ–º—ã—Ö —è–∑—ã–∫–æ–≤
export const locales = ['ru', 'en', 'de', 'fr', 'es', 'zh', 'ja', 'ar'];
export const defaultLocale = 'ru';

// –ó–∞–≥—Ä—É–∑–∫–∞ —Å–æ–æ–±—â–µ–Ω–∏–π –¥–ª—è —É–∫–∞–∑–∞–Ω–Ω–æ–≥–æ —è–∑—ã–∫–∞
export default getRequestConfig(async ({ locale }) => {
  // –ü—Ä–æ–≤–µ—Ä–∫–∞ –ø–æ–¥–¥–µ—Ä–∂–∏–≤–∞–µ–º–æ–≥–æ —è–∑—ã–∫–∞
  if (!locales.includes(locale as any)) notFound();

  // –î–∏–Ω–∞–º–∏—á–µ—Å–∫–∞—è –∑–∞–≥—Ä—É–∑–∫–∞ –ø–µ—Ä–µ–≤–æ–¥–æ–≤
  return {
    messages: (await import(`../data/locales/${locale}.json`)).default
  };
});

// –•—É–∫ –¥–ª—è –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è –ø–µ—Ä–µ–≤–æ–¥–æ–≤ –≤ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞—Ö
export function useTranslation() {
  const { messages } = useRequestContext();
  return (key: string, params?: Record<string, string | number>) => {
    let value = messages[key] || key;

    // –ü–æ–¥—Å—Ç–∞–Ω–æ–≤–∫–∞ –ø–∞—Ä–∞–º–µ—Ç—Ä–æ–≤
    if (params) {
      Object.entries(params).forEach(([key, value]) => {
        value = value.replace(`{${key}}`, String(value));
      });
    }

    return value;
  };
}

// –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–æ–µ –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–∏–µ —è–∑—ã–∫–∞
export function detectLanguage(headers: Headers): string {
  const acceptLanguage = headers.get('accept-language');
  if (!acceptLanguage) return defaultLocale;

  // –ò–∑–≤–ª–µ—á–µ–Ω–∏–µ –∫–æ–¥–∞ —è–∑—ã–∫–∞ –∏–∑ –∑–∞–≥–æ–ª–æ–≤–∫–∞
  const langCode = acceptLanguage.split(',')[0].split('-')[0].toLowerCase();

  // –°–æ–ø–æ—Å—Ç–∞–≤–ª–µ–Ω–∏–µ —Å –ø–æ–¥–¥–µ—Ä–∂–∏–≤–∞–µ–º—ã–º–∏ —è–∑—ã–∫–∞–º–∏
  const languageMap: Record<string, string> = {
    'ru': 'ru',
    'en': 'en',
    'de': 'de',
    'fr': 'fr',
    'es': 'es',
    'zh': 'zh',
    'ja': 'ja',
    'ar': 'ar',
    'uk': 'ru',  // –£–∫—Ä–∞–∏–Ω—Å–∫–∏–π ‚Üí –†—É—Å—Å–∫–∏–π
    'be': 'ru',  // –ë–µ–ª–æ—Ä—É—Å—Å–∫–∏–π ‚Üí –†—É—Å—Å–∫–∏–π
    'kk': 'ru'   // –ö–∞–∑–∞—Ö—Å–∫–∏–π ‚Üí –†—É—Å—Å–∫–∏–π
  };

  return languageMap[langCode] || defaultLocale;
}
```

