    // –û—Ç–ø—Ä–∞–≤–∫–∞ –≤ Telegram
    const telegramMessage = `
üë§ ${t('feedback.name', locale)}: ${name}
üìß ${t('feedback.email', locale)}: ${email || t('feedback.notProvided', locale)}
üì± ${t('feedback.contactMethod', locale)}: ${contactMethod || t('feedback.notSpecified', locale)}

üí¨ ${t('feedback.message', locale)}:
${message}
    `;

    await sendTelegramMessage(telegramMessage, locale);

    // –û—Ç–ø—Ä–∞–≤–∫–∞ –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏—è
    if (contactMethod === 'email' && email) {
      await sendEmailConfirmation(email, name, locale);
    } else if (contactMethod === 'telegram') {
      await sendTelegramConfirmation(name, locale);
    }

    return NextResponse.json({ 
      success: true,
      message: t('feedback.success', locale)
    });
  } catch (error) {
    console.error('Feedback error:', error);
    return NextResponse.json(
      { error: t('feedback.failed', locale) }, 
      { status: 500 }
    );
  }
}
