// components/ui/ResponsiveContainer.tsx
'use client';

import { useState, useEffect } from 'react';
import { useTranslations } from 'next-intl';

export function ResponsiveContainer({ children }: { children: React.ReactNode }) {
  const t = useTranslations('layout');
  const [orientation, setOrientation] = useState<'portrait' | 'landscape'>('portrait');
  const [containerHeight, setContainerHeight] = useState('100vh');

  useEffect(() => {
    const updateLayout = () => {
      const isLandscape = window.innerWidth > window.innerHeight;
      setOrientation(isLandscape ? 'landscape' : 'portrait');

      // –ê–¥–∞–ø—Ç–∞—Ü–∏—è –≤—ã—Å–æ—Ç—ã –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä–∞
      if (isLandscape) {
        setContainerHeight('100vh');
      } else {
        setContainerHeight('calc(100vh - 60px)'); // –ú–∏–Ω—É—Å –≤—ã—Å–æ—Ç–∞ —Ö–µ–¥–µ—Ä–∞
      }
    };

    window.addEventListener('resize', updateLayout);
    updateLayout();

    return () => window.removeEventListener('resize', updateLayout);
  }, []);

  return (
    <div 
      className={`transition-all duration-300 ${
        orientation === 'landscape' 
          ? 'flex flex-row h-screen' 
          : 'flex flex-col min-h-screen'
      }`}
      style={{ height: containerHeight }}
      aria-label={t('containerOrientation', { orientation })}
    >
      {children}
    </div>
  );
}
```

